import React,{useEffect,useState} from 'react'

const Product = () => {
    const [item, setItem] = useState([])
   
    useEffect(()=>{
       const getProduct=async()=>{
           const product=await fetch("https://fakestoreapi.com/products")
           const result=product.json()
           setItem(result)
          

       }
       getProduct()
       
    },[])
    console.log(item);


  return (
    <>
    <h1>E-commerce</h1>
    {
        item.map((value)=>{
            return(
                <h1>
                    {value.category}
                </h1>
            )
        })
    }
  
    </>
  )
}

export default Product
